  double _getPageWidth(
      PdfPageFormat format, pw.PageOrientation pageOrientation) {
    double width;
    double height;

    switch (format) {
      case PdfPageFormat.a4:
        width = 793.7007874;
        height = 1122.519685;
        break;
      case PdfPageFormat.a3:
        width = 1122.519685;
        height = 1587.4015748;
        break;
      case PdfPageFormat.a5:
        width = 559.37007874;
        height = 793.7007874;
        break;
      case PdfPageFormat.a6:
        width = 396.8503937;
        height = 559.37007874;
        break;
      case PdfPageFormat.letter:
        width = 816;
        height = 1056;
        break;
      case PdfPageFormat.legal:
        width = 816;
        height = 1344;
        break;
      default:
        width = 1240; // 2480 / 2
        height = 1754; // 3508 / 2
        break;
    }

    // Swap width and height if orientation is landscape
    return pageOrientation == pw.PageOrientation.landscape ? height : width;
  }

