  BorderRadius setBorderRadius(
      String s, double value, ItemDecoration itemDecoration) {
    bool isNotNull = itemDecoration.decoration.borderRadius != null;
    switch (s) {
      case 'topLeft':
        return BorderRadius.only(
          topLeft: Radius.circular(value),
          topRight: isNotNull
              ? (itemDecoration.decoration.borderRadius as BorderRadius)
                  .topRight
              : Radius.circular(0),
          bottomLeft: isNotNull
              ? (itemDecoration.decoration.borderRadius as BorderRadius)
                  .bottomLeft
              : Radius.circular(0),
          bottomRight: isNotNull
              ? (itemDecoration.decoration.borderRadius as BorderRadius)
                  .bottomRight
              : Radius.circular(0),
        );
      case 'topRight':
        return BorderRadius.only(
          topLeft: isNotNull
              ? (itemDecoration.decoration.borderRadius as BorderRadius).topLeft
              : Radius.circular(0),
          topRight: Radius.circular(value),
          bottomLeft: isNotNull
              ? (itemDecoration.decoration.borderRadius as BorderRadius)
                  .bottomLeft
              : Radius.circular(0),
          bottomRight: isNotNull
              ? (itemDecoration.decoration.borderRadius as BorderRadius)
                  .bottomRight
              : Radius.circular(0),
        );
      case 'bottomLeft':
        return BorderRadius.only(
          topLeft: isNotNull
              ? (itemDecoration.decoration.borderRadius as BorderRadius).topLeft
              : Radius.circular(0),
          topRight: isNotNull
              ? (itemDecoration.decoration.borderRadius as BorderRadius)
                  .topRight
              : Radius.circular(0),
          bottomLeft: Radius.circular(value),
          bottomRight: isNotNull
              ? (itemDecoration.decoration.borderRadius as BorderRadius)
                  .bottomRight
              : Radius.circular(0),
        );
      case 'bottomRight':
        return BorderRadius.only(
          topLeft: isNotNull
              ? (itemDecoration.decoration.borderRadius as BorderRadius).topLeft
              : Radius.circular(0),
          topRight: isNotNull
              ? (itemDecoration.decoration.borderRadius as BorderRadius)
                  .topRight
              : Radius.circular(0),
          bottomLeft: isNotNull
              ? (itemDecoration.decoration.borderRadius as BorderRadius)
                  .bottomLeft
              : Radius.circular(0),
          bottomRight: Radius.circular(value),
        );
      case 'all':
        return BorderRadius.all(Radius.circular(value));

      default:
        return BorderRadius.all(Radius.circular(value));
    }
  }


